package learnJava;

public class ifelseifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int E = 10;
		
		if (E > 10) {
			
			System.out.println("E is more than 10");
			
			} else if (E < 10) {
				
				System.out.println("E is less than 10");
				
			}else {
				
				System.out.println("E equal 10");
			}
			
			
	
	}

}
